"""
字体管理模块
处理 PDF 生成中的中文字体问题
"""
import os
import base64
from reportlab.pdfbase import pdfmetrics
from reportlab.pdfbase.ttfonts import TTFont

# SimHei 字体的 base64 编码（黑体）
# 这是一个精简版的字体，用于基本的中文显示
SIMHEI_FONT_BASE64 = """

"""


def register_chinese_font():
    """注册中文字体"""
    try:
        # 首先尝试使用系统字体
        font_paths = [
            ('C:\\Windows\\Fonts\\simhei.ttf', 0),
            ('C:\\Windows\\Fonts\\msyh.ttc', 0),
            ('C:\\Windows\\Fonts\\simsun.ttc', 0),
            ('/usr/share/fonts/truetype/wqy/wqy-zenhei.ttc', 0),
            ('/usr/share/fonts/truetype/wqy/wqy-microhei.ttc', 0),
            ('/usr/share/fonts/opentype/noto/NotoSansCJK-Regular.ttc', 0),
            ('/System/Library/Fonts/PingFang.ttc', 1),
            ('/System/Library/Fonts/STHeiti Light.ttc', 0),
        ]

        for font_path, subfont_index in font_paths:
            try:
                if os.path.exists(font_path):
                    pdfmetrics.registerFont(TTFont('SimHei', font_path, subfontIndex=subfont_index))
                    pdfmetrics.registerFont(TTFont('SimHei-Bold', font_path, subfontIndex=subfont_index))
                    print(f"Successfully registered system font: {font_path}")
                    return True
            except Exception as e:
                print(f"Failed to register font {font_path}: {e}")
                continue

        # 如果系统字体不可用，使用内置字体（略）
        # 由于 base64 字体太大，我们改用 reportlab 的默认字体
        print("Warning: No system Chinese font found, using default fonts")
        print("PDF may not display Chinese characters correctly")
        return False

    except Exception as e:
        print(f"Error registering Chinese font: {e}")
        return False


def get_chinese_font_name():
    """获取中文字体名称"""
    # 检查是否已注册中文字体
    try:
        pdfmetrics.getFont('SimHei')
        return 'SimHei'
    except:
        return 'Helvetica'


def get_chinese_bold_font_name():
    """获取中文字体名称（粗体）"""
    # 检查是否已注册中文字体
    try:
        pdfmetrics.getFont('SimHei-Bold')
        return 'SimHei-Bold'
    except:
        return 'Helvetica-Bold'
